const header=document.querySelector(".header");window.addEventListener("scroll",()=>{window.scrollY>80?header.classList.add("header_active"):header.classList.remove("header_active")});const menuLinks=document.querySelectorAll(".header__link"),burger=document.querySelector(".burger"),menu=document.querySelector(".header__nav"),burgerLines=burger.querySelectorAll(".burger__line"),burgerMenuHandler=()=>{menu.classList.toggle("header__nav_active"),burgerLines.forEach(e=>{e.classList.toggle("active")})};burger.addEventListener("click",burgerMenuHandler),window.innerWidth<=768&&menuLinks.forEach(e=>{e.addEventListener("click",burgerMenuHandler)});const menuHeight=document.querySelector(".header").offsetHeight;menuLinks.forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const r=this.getAttribute("href").slice(1),t=document.getElementById(r);if(t){const e=t.getBoundingClientRect().top-menuHeight;window.scrollTo({top:window.scrollY+e,behavior:"smooth"})}})});